mapper.select:

select no,name,email,gender,role 
  from user 
where no= #{no}


select no 
  from user 
where email= #{email}

choose.when:

	update user 
  	  set name=#{name},
  	  	  password=password(#{password}), 
  	  	  gender=#{gender} 
	where no=#{no} and email=#{email}

choose.otherwise:

	update user 
  	  set name=#{name}, 
  	  	  gender=#{gender} 
	where no=#{no} and email=#{email}
